#!/usr/bin/env python3
"""
Decode Binary Messages from EXODUS and GENESIS Tokens
"""

def decode_binary(binary_str):
    """Decode space-separated binary to text"""
    bytes_list = binary_str.strip().split()
    result = ""
    for b in bytes_list:
        if len(b) == 8 and all(c in '01' for c in b):
            result += chr(int(b, 2))
    return result

